mapper.select:
SELECT project_id AS projectId, project_name AS projectName, seller AS seller, private_key AS privateKey, public_key AS publicKey, created_at AS createdAt, updated_at AS updatedAt FROM bb_alipay
project_id = #{criteria.projectId}
ORDER BY id DESC LIMIT #{criteria.start},#{criteria.length}

mapper.update:
UPDATE bb_alipay
SET seller      = #{seller},
    private_key = #{privateKey},
    public_key  = #{publicKey}
WHERE project_id = #{projectId}
