mapper.select:
SELECT count(1) AS recordsTotal FROM bb_orders LEFT JOIN bb_order_appointments ON bb_orders.id = bb_order_appointments.order_id INNER JOIN bb_rooms ON bb_rooms.id = bb_order_appointments.room_id INNER JOIN bb_staffs ON bb_rooms.project_id = bb_staffs.id WHERE bb_staffs.project_id = #{staff.projectId}
AND CONCAT(DATE_FORMAT(bb_orders.created_at, '%Y%m%d'), LPAD(bb_orders.id, 6, 0)) LIKE CONCAT('%',#{criteria.orderNo},'%')
AND bb_orders.created_at >= #{criteria.startDate}
AND #{criteria.endDate} >= bb_orders.created_at
AND bb_order_appointments.contact_mobile LIKE CONCAT('%',#{criteria.mobile},'%')

SELECT bb_orders.id, CONCAT(DATE_FORMAT(bb_orders.created_at, '%Y%m%d'), LPAD(bb_orders.id, 6, 0)) AS orderNo, bb_orders.account_id AS accountId, bb_orders.account_name AS accountName, bb_orders.total, bb_orders.pay_amount AS payAmount, bb_orders.coupon, bb_orders.status, bb_orders.remark, bb_orders.created_at AS createdAt, bb_orders.updated_at AS updatedAt FROM bb_orders LEFT JOIN bb_order_appointments ON bb_orders.id = bb_order_appointments.order_id INNER JOIN bb_rooms ON bb_rooms.id = bb_order_appointments.room_id INNER JOIN bb_staffs ON bb_rooms.project_id = bb_staffs.id WHERE bb_staffs.project_id = #{staff.projectId}
AND CONCAT(DATE_FORMAT(bb_orders.created_at, '%Y%m%d'), LPAD(bb_orders.id, 6, 0)) LIKE CONCAT('%',#{criteria.orderNo},'%')
AND bb_orders.created_at >= #{criteria.startDate}
AND #{criteria.endDate} >= bb_orders.created_at
AND bb_order_appointments.contact_mobile LIKE CONCAT('%',#{criteria.mobile},'%')
ORDER BY id DESC LIMIT #{criteria.start},#{criteria.length}

SELECT bb_orders.id, CONCAT(DATE_FORMAT(bb_orders.created_at, '%Y%m%d'), LPAD(bb_orders.id, 6, 0)) AS order_no, bb_orders.account_id, bb_orders.account_name, bb_orders.total, bb_orders.pay_amount, bb_orders.coupon, bb_orders.status, bb_orders.remark, bb_orders.created_at, bb_orders.updated_at, CASE WHEN bb_invoices.id IS NULL THEN false ELSE true END AS invoice, bb_rooms.id AS appointed_room_id, bb_rooms.name AS appointed_room_name, CONCAT('https://bb-platform.oss-cn-hangzhou.aliyuncs.com/',bb_rooms.cover) AS appointed_room_cover, bb_order_appointments.start_time AS appointed_start_time, bb_order_appointments.end_time AS appointed_end_time, bb_order_appointments.contact_name AS appointed_contact_name, bb_order_appointments.contact_mobile AS appointed_contact_mobile, CONCAT(bb_rooms.project_name,bb_rooms.building_no,bb_rooms.floor_no,bb_rooms.room_no) AS appointed_room_address FROM bb_orders LEFT JOIN bb_invoices ON bb_orders.id = bb_invoices.order_id LEFT JOIN bb_order_appointments ON bb_orders.id = bb_order_appointments.order_id INNER JOIN bb_rooms ON bb_order_appointments.room_id = bb_rooms.id WHERE bb_orders.account_id = #{account.accountId}
AND status = #{status.code}
ORDER BY id DESC LIMIT ${(page - 1) * length},#{length}

SELECT bb_orders.id, CONCAT(DATE_FORMAT(bb_orders.created_at, '%Y%m%d'), LPAD(bb_orders.id, 6, 0)) AS order_no, bb_orders.account_id, bb_orders.account_name, bb_orders.total, bb_orders.pay_amount, bb_orders.coupon, bb_orders.status, bb_orders.remark, bb_orders.created_at, bb_orders.updated_at, bb_rooms.id AS appointed_room_id, bb_rooms.name AS appointed_room_name, CONCAT('https://bb-platform.oss-cn-hangzhou.aliyuncs.com/',bb_rooms.cover) AS appointed_room_cover, bb_order_appointments.start_time AS appointed_start_time, bb_order_appointments.end_time AS appointed_end_time, bb_order_appointments.contact_name AS appointed_contact_name, bb_order_appointments.contact_mobile AS appointed_contact_mobile, CONCAT(bb_rooms.project_name,bb_rooms.building_no,bb_rooms.floor_no,bb_rooms.room_no) AS appointed_room_address FROM bb_orders LEFT JOIN bb_order_appointments ON bb_orders.id = bb_order_appointments.order_id INNER JOIN bb_rooms ON bb_order_appointments.room_id = bb_rooms.id WHERE bb_rooms.project_id IN
#{staff.projectId}
AND bb_orders.status IN (0,2)
AND bb_orders.status IN (1,3)
LIMIT ${(page - 1) * length},#{length}

mapper.update:
update bb_orders
set status = #{status}
WHERE CONCAT(DATE_FORMAT(bb_orders.created_at, '%Y%m%d'), LPAD(bb_orders.id, 6, 0)) = #{orderNo}

update bb_orders
set status   = #{status},
    pay_type = #{payType}
WHERE CONCAT(DATE_FORMAT(bb_orders.created_at, '%Y%m%d'), LPAD(bb_orders.id, 6, 0)) = #{id}
