mapper.select:
SELECT count(1) AS recordsTotal FROM bb_rooms
WHERE project_id = #{staff.projectId}

    AND name LIKE CONCAT('%',#{criteria.name},'%')


    AND layer_area >= #{criteria.layerAreaFrom}


    AND #{criteria.layerAreaTo} >= layer_area


    AND quota >= #{criteria.quotaFrom}


    AND #{criteria.quotaTo} >= quota

SELECT bb_rooms.id, bb_rooms.serial_no AS serialNo, bb_rooms.name, bb_rooms.type, bb_rooms.publish, bb_rooms.rental, bb_rooms.building_no AS buildingNo, bb_rooms.floor_no AS floorNo, bb_rooms.room_no AS roomNo, bb_rooms.price, bb_rooms.layer_area AS layerArea, bb_rooms.quota, bb_rooms.supervisor, bb_rooms.supervisor_mobile AS supervisorMobile, bb_rooms.supervisor_email AS supervisorEmail, bb_rooms.free_service AS freeService, bb_rooms.online AS online FROM bb_rooms WHERE project_id = #{staff.projectId}
AND name LIKE CONCAT('%',#{criteria.name},'%')
AND layer_area >= #{criteria.layerAreaFrom}
AND #{criteria.layerAreaTo} >= layer_area
AND quota >= #{criteria.quotaFrom}
AND #{criteria.quotaTo} >= quota
ORDER BY id DESC
LIMIT #{start},#{length}

SELECT bb_rooms.id, bb_rooms.name
FROM bb_rooms
WHERE project_id = #{staff.projectId}

SELECT id, serial_no AS serialNo, CONCAT(project_name,name) AS name, CONCAT('https://bb-platform.oss-cn-hangzhou.aliyuncs.com/',cover) AS cover, type, publish, rental, building_no AS buildingNo, floor_no AS floorNo, room_no AS roomNo, price, layer_area AS layerArea, quota, supervisor, supervisor_mobile AS supervisorMobile, supervisor_email AS supervisorEmail, free_service AS freeService FROM bb_rooms WHERE online = true AND (publish = 1 OR project_id IN
#{staff.projectId}
)
AND name LIKE CONCAT('%',#{query.keyword},'%')
AND quota > #{query.quota}
ORDER BY ${query.sort.column} ${query.sort.order} LIMIT #{query.start},#{query.pageSize}

mapper.update:
UPDATE bb_rooms
SET online = true
where id = #{id}

UPDATE bb_rooms
SET online = false
where id = #{id}
