mapper.select:
SELECT count(1) AS recordsTotal FROM bb_supports

    project_id = #{staff.projectId}

        and name LIKE "%${keyword}%"

select * from bb_supports

    project_id = #{staff.projectId}

        and name LIKE "%${keyword}%"


order by bb_supports.id desc LIMIT #{start},#{length}

mapper.update:
update bb_supports
set name  = #{support.name},
    cover = #{support.cover},
    unit  = #{support.unit}
where id = #{support.id}
