mapper.select:
SELECT project_id AS projectId, project_name AS projectName, app_id AS appId, mch_id AS mchId, secret_key AS secretKey, created_at AS createdAt, updated_at AS updatedAt FROM bb_wxpay
project_id = #{criteria.projectId}
ORDER BY id DESC LIMIT #{criteria.start},#{criteria.length}

mapper.update:
UPDATE bb_wxpay
SET app_id     = #{appId},
    mch_id     = #{mchId},
    secret_key = #{secretKey}
WHERE project_id = #{projectId}
